import React, { useEffect, useState } from 'react';

function ResourcesPage() {
  const [resources, setResources] = useState([]);
  const [name, setName] = useState('');
  const [type, setType] = useState('');
  const [description, setDescription] = useState('');
  const [error, setError] = useState('');
  const token = localStorage.getItem('token');
  const role = localStorage.getItem('role');

  useEffect(() => {
    fetch('http://localhost:5000/api/resources/', {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(res => res.json())
      .then(setResources);
  }, [token]);

  const handleAdd = async (e) => {
    e.preventDefault();
    setError('');
    try {
      const res = await fetch('http://localhost:5000/api/resources/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`
        },
        body: JSON.stringify({ name, type, description })
      });
      if (res.status === 201) {
        setName(''); setType(''); setDescription('');
        const updated = await fetch('http://localhost:5000/api/resources/', {
          headers: { Authorization: `Bearer ${token}` }
        }).then(r => r.json());
        setResources(updated);
      } else {
        const data = await res.json();
        setError(data.msg || 'Erro ao adicionar recurso');
      }
    } catch {
      setError('Erro de conexão');
    }
  };

  const handleDelete = async (id) => {
    try {
      await fetch(`http://localhost:5000/api/resources/${id}`, {
        method: 'DELETE',
        headers: { Authorization: `Bearer ${token}` }
      });
      setResources(resources.filter(r => r.id !== id));
    } catch {}
  };

  return (
    <div>
      <h2>Recursos</h2>
      {role === 'admin' && (
        <form onSubmit={handleAdd}>
          <input placeholder="Nome" value={name} onChange={e => setName(e.target.value)} required />
          <input placeholder="Tipo" value={type} onChange={e => setType(e.target.value)} required />
          <input placeholder="Descrição" value={description} onChange={e => setDescription(e.target.value)} />
          <button type="submit">Adicionar</button>
          {error && <p className="error">{error}</p>}
        </form>
      )}
      <ul>
        {resources.map(r => (
          <li key={r.id}>
            {r.name} ({r.type}) - {r.description}
            {role === 'admin' && (
              <button onClick={() => handleDelete(r.id)}>Remover</button>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default ResourcesPage;
